select [Номер_договора] as 'Номер договора',[Наименование] as 'Тип отходов',SUM([Изменение_накопления]) as Накопение from [dbo].[Клиенты] K inner join [dbo].[Накопление_клиентов] N on K.[Id_клиента] = N.[Id_клиента] inner join [dbo].[Тип_отходов] T on N.Id_Типа = T.Id_Типа group by [Номер_договора],[Наименование]  order by [Номер_договора]